<template>
<div v-if="order">
<div class="card">
    <div class="card-header">
	<div class="card-header-title" v-on:click="open = !open">
	    <span class="icon" v-bind:class="skip ? 'has-text-warning' : validAttributes ? 'has-text-success' : 'has-text-danger' ">
		<font-awesome-icon :icon="skip ? 'circle' : validAttributes ? 'check' : 'times'">
		</font-awesome-icon>
	    </span>
	    <span class="icon" v-bind:class="skip ? 'has-text-warning' : validItems ? 'has-text-success' : 'has-text-danger' ">
		<font-awesome-icon :icon="skip ? 'circle' : validItems ? 'check' : 'times'">
		</font-awesome-icon>
	    </span>
	    {{ customer_name }}
	</div>
	<div class="card-header-icon" >
	    <span class="icon has-text-primary">
		<font-awesome-icon :icon="open ? 'angle-down' : 'angle-left'"></font-awesome-icon>
	    </span>
	</div>
    </div>
    <div class="card-content" v-bind:class="{'is-hidden' : !open}">
	<div class="field">
	  <input v-model="skip" @input="changeEnabled" class="is-checkradio" :id="'skip-' + id" type="checkbox">
	  <label :for="'skip-' + id">Skip?</label>
	</div>
	<div class="field" v-if="notes">
	    Notes
	    <div class="control">
		<input class="input" :value="notes" readonly>
	    </div>
	</div>
	<strong>Customer Name: {{ customer_name }}</strong>
	<div class="control">
	    <input class="input" v-bind:class="{ 'is-warning' : customer_loading, 'is-danger' : !customer_valid && !customer_loading, 'is-success' : customer_valid }" v-model="customer_id" type="text" v-on:input="verifyCustomer">
	</div>
	<br/>
	<strong>Salesrep Name: {{ salesrep_name }}</strong>
	<div class="control">
	    <input class="input" v-bind:class="{ 'is-warning' : salesrep_loading, 'is-danger' : !salesrep_valid && !salesrep_loading, 'is-success' : salesrep_valid }" v-model="salesrep_id" type="text" v-on:input="verifySalesrep">
	</div>
	<br/>
	<strong>Items:</strong>
	<div class="columns is-multiline">
	    <item v-for="item in items" :item_id="item" :order_id="order_id" :key='item'></item>
	</div>
    </div>
</div>
<br/>
</div>
</template>

<style>
is-hidden {
    display: none;
}
</style>

<script>

import { getApi } from '../api';

import Item from './Item';

export default {
    props: [ 'order_id' ],
    data: function() {
	return {
	    open: false,
	    customer_loading: false,
	    customer_timeout: null,
	    salesrep_loading: false,
	    salesrep_timeout: null
	}
    },
    computed: {
	order: {
	    set(body) {
		this.$store.commit('setOrder',  {
		    _id: this.order_id,
		    value: body
		});
	    },
	    get() {
		if(!this.order_id in this.$store.state.orders)
		    return false;
		return this.$store.state.orders[this.order_id];
	    }
	},
	items: function() {
	    return this.order.items;
	},
	customer_name: {
	    get() { return this.order.customer_name }
	},
	customer_id: {
	    set(body) { this.order = {...this.order, customer_id : body} },
	    get() { return this.order.customer_id }
	},
	customer_valid: {
	    set(body) { this.order = {...this.order, customer_valid : body} },
	    get() { return this.order.customer_valid }
	},
	salesrep_id: {
	    set(body) { this.order = {...this.order, salesrep_id : body} },
	    get() { return this.order.salesrep_id }
	},
	salesrep_name: {
	    get() { return this.order.salesrep_name }
	},
	salesrep_valid: {
	    set(body) { this.order = {...this.order, salesrep_valid : body} },
	    get() { return this.order.salesrep_valid }
	},
	skip: {
	    // Reverse of enabled
	    set(body) { this.order = {...this.order, enabled : !body} },
	    get() { return !this.order.enabled }
	},
	notes: {
	    get() { return this.order.notes }
	},
	id: {
	    get() { return this.order._id }
	},
	validAttributes: function() {
	    return this.customer_valid && this.salesrep_valid;
	},
	validItems: function() {
	    for(var i = 0; i < this.items.length; i++) {
		// Dereference the item
		var it = this.$store.state.items[this.items[i]];
		if(!it || !it.valid)
		    return false;
	    }
	    return true;
	}
    },
    methods: {
	verifyCustomer: async function() {
	    // Check if field is a number
	    var isNumeric = !isNaN(parseInt(this.customer_id));
	    if(!isNumeric) {
		this.customer_valid = false;
		return;
	    }
	    // Look up
	    this.customer_loading = true;
	    clearTimeout(this.customer_timeout);
	    this.customer_timeout = setTimeout(async function() {
		let result = await getApi(
		    `/order/${this.id}/modify_customer?internal_id=${this.customer_id}`);
		// Save to store
		this.customer_valid = result.status == 'valid';
		this.customer_loading = false;

	    }.bind(this), 1000);
	},
	verifySalesrep: async function() {
	    // Check if field is a number
	    var isNumeric = !isNaN(parseInt(this.salesrep_id));
	    if(!isNumeric) {
		this.salesrep_valid = false;
		return;
	    }
	    // Look up
	    this.salesrep_loading = true;
	    clearTimeout(this.salesrep_timeout);
	    this.salesrep_timeout = setTimeout(async function() {
		let result = await getApi(
		    `/order/${this.id}/modify_salesrep?internal_id=${this.salesrep_id}`);
		this.salesrep_valid = result.status == 'valid';
		this.salesrep_loading = false;

	    }.bind(this), 1000);
	},
	changeEnabled: async function() {
	    let result = await getApi(`/order/${this.id}/changeEnabled?enabled=${this.skip}`)
	}
    },
    components: {
	Item
    }
}
</script>

